INSERT INTO shader
(id, title, shader_code, preview_img, author_id, created_at)
VALUES('a074ae27-0857-4371-92c6-304cbcbe9f52', 'Purple Vision', '
void pMod1(inout float p, float size) {
	float halfsize = size*0.5;
	p = mod(p + halfsize, size) - halfsize;
}

void pMod3(inout vec3 p, vec3 size) {
	p = mod(p - size*0.5, size) - size*0.5;
}

float sphere(vec3 p, float radius){
    return length(p)-radius;
}


float map(vec3 p)
{
    vec3 q = p;
	vec3 qa = p;
    pMod3(q, vec3(0.8, 1., 0.23));
	pMod3(qa, vec3(0.8, 1., 0.18));
    pMod1(p.x, 1.);

    float s1 = sphere(p, 0.75);
    float s2 = sphere(q, 0.5);
    float s3 = sphere(qa, 0.555);

  	float df1 = min(min(s1, s2),s3); // Union

    return df1;
}


float trace(vec3 origin, vec3 r)
{
  float t = 0.0;
    for (int i = 0; i < 64; ++i) {
        vec3 p = origin + r * t;
        float d = map(p);
        t += d*0.22;
        }
    return t;
}


void main()
{
    vec2 uv = gl_FragCoord.xy/RESOLUTION.xy;
    vec3 color = vec3(0.324, 0.12, 0.536);
    uv = uv *2.-1.; // Remap the space to -1. to 1.
    uv.x *= RESOLUTION.x/RESOLUTION.y;


   	float FOV = 1.25;
   	vec3 ray = normalize(vec3(uv, FOV));


    vec3 origin = vec3(TIME, 0.0, -1.75);
    float t = trace(origin, ray);

    float expFog = 0.5 / (t*t* 0.95);

    vec3 fc = vec3(expFog);

    if ( t < -0.9 ) {
        color = 0.25-(0.5/t+color);
        vec3 maincolor = exp(-(fc+color)*3.6);
        color = (1.0 - maincolor);
        color = cos(color) * maincolor;
    }
    else
        color = vec3(0);

    gl_FragColor = vec4(color,dot(color, vec3(0.5)) * exp(-expFog));
}', 'data:image/jpeg;base64,', 'f6acdbc8-f257-4ad0-b343-39d5dbe9755a'::uuid, '2022-06-19');


INSERT INTO shader
(id, title, shader_code, preview_img, author_id, created_at)
VALUES('c1b4ca11-4cfc-4cee-b4b9-75e73eb941b6', 'Colored Pump', 'float vmax(vec2 v) {return max(v.x, v.y);}
float fBox2(vec2 p, vec2 b) {return vmax(abs(p)-b);}

mat2 rot(float a) {float s=sin(a), c=cos(a); return mat2(c,s,-s,c);}
float wf1(vec2 p){return sin(p.x) + cos(p.y);}

float cappedCylinder(vec3 p, float h, float r){
    vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(r, h);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

vec3 gl = vec3(0.);
float gl1 = 0.;
vec3 gl2 = vec3(0.);

vec3 map(vec3 p) {
    vec3 r = vec3(0.);
    vec3 d = vec3(0.);
    p.xz *= rot(TIME * .5);
    vec3 m = p;
    p.xz *= rot(sin(-p.y * .5) * 1.1);
    p.xz = abs(p.zx)-vec2(.8);
    float i = sin(p.y * 3. + TIME * 10.) * .5 + .5;
    float b = cappedCylinder(p,  5.5 , ((i - .5) * 2. * .3) * cos(p.y * .2));
    gl += (.0004/(.01+b*b)) * mix(vec3(1.,0.,1.), vec3(1.,1.,0.), p.y);
    r.x = max(cappedCylinder(p, 2., .3 + .2 * i), -cappedCylinder(p, 3., .2 + .25 * i));
    p.xz *= rot(p.y * 3. + TIME * 2.);
    vec3 q = p;
    q.xz *= rot(3.14/2.);
    if (fBox2(p.xy, vec2(.2, 10.)) < 0.) r.yz = vec2(3.,0.);
    else if (fBox2(q.xy, vec2(.2, 10.)) < 0.) r.yz = vec2(4., 0.);
    else r.yz = vec2(1.);
    gl1 += (.000001/(.000001+pow(r.x+.003, 2.)));
    d.x = min(r.x, cappedCylinder(p, 8.5 , (.25 + (i - .5) * 2. * .15) * cos(p.y * .2)));
    d.y = 2.;
    if (r.x > d.x) r = d;
    p = m;
    d.x = length(p) - .45 - .1 * (sin(TIME * 10.) * .5 + .5);
    gl2 += (.0006/(.01+d.x*d.x)) * mix(vec3(1.,0.,1.), vec3(1.,1.,0.), m.y);
    if (r.x > d.x) r = d;
    p = m;
    if (p.y > 0.) p.xz *= rot(.3);
    p = abs(p);
    p.zx *= rot(-3.14/4.);
    p.xy *= rot(-3.14/4.);
    p.y -= 1.;
    q = p;
    p.yx *= rot( sin(p.y * 3.14) * .3 );
    d.x =  cappedCylinder(p, 1. , (.06 + (i - .5) * 2. * .04));
    p = q; p.y -= 1.;
    d.x = min(d.x, length(p) - .15 - .05 * (sin(TIME * 10. + 1.5) * .5 + .5));
    gl2 += (.0003/(.01+d.x*d.x)) * mix(vec3(1.,0.,1.), vec3(1.,1.,0.), -m.y);
    d.y = 2.;
    if (r.x > d.x) r = d;
    return r;
}

const vec2 e = vec2(.00035, -.00035);
vec3 norm(vec3 po) {
        return normalize(e.yyx*map(po+e.yyx).x + e.yxy*map(po+e.yxy).x +
                         e.xyy*map(po+e.xyy).x + e.xxx*map(po+e.xxx).x);
}

void main()
{
    vec2 uv = (gl_FragCoord.xy - RESOLUTION.xy * .5) / RESOLUTION.y;
    vec3 ro = vec3(0.,3.,-6.),
         rd = normalize(vec3(uv,1.)),
         p, h;
    rd.yz *= rot(-.4);
    float t = 0.;
    for(int i = 0; i < 120; i++) {
         p = ro + rd * t;
         h = map(p);
         if (h.x<.0001||t>40.) {
             if (h.z == 1.) h.x = abs(h.x) + .0001;
             else break;
         };
         t += h.x * .7;
    }
    vec3 ld = vec3(0., 1.,0.);
    vec3 ld1 = vec3(3., 3., 0.);
    ld1.xz *= rot(TIME * .3);
    vec3 col = vec3(.1);
    if (h.x<.0001) {
        if (h.y == 1.) col = vec3(.1, .3, .2);
        if (h.y == 2.) col = vec3(.7,.7,.3);
        if (h.y == 3.) col = vec3(.5, .9, .5);
        if (h.y == 4.) col = vec3(.5, .5, .9);
    }
    col = mix(col, vec3(.1, .3, .2), clamp(gl1,0.,1.));
    col += gl;
    col += gl2;
    gl_FragColor = vec4(col,1.0);
}', 'data:image/jpeg;base64,', 'f6acdbc8-f257-4ad0-b343-39d5dbe9755a'::uuid, '2022-06-19');


INSERT INTO shader
(id, title, shader_code, preview_img, author_id, created_at)
VALUES('0d8ad360-7b97-4fbd-85dc-2e82557318bd', 'Animating Hexagons', '
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))
#define PI          3.141592654
#define TAU         (2.0*PI)

float sRGB(float t) { return mix(1.055*pow(t, 1./2.4) - 0.055, 12.92*t, step(t, 0.0031308)); }
vec3 sRGB(in vec3 c) { return vec3 (sRGB(c.x), sRGB(c.y), sRGB(c.z)); }

float tanh_approx(float x) {
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}

#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

float hash(vec2 co) {
  return fract(sin(dot(co.xy ,vec2(12.9898,58.233))) * 13758.5453);
}

vec2 hextile(inout vec2 p) {
  const vec2 sz       = vec2(1.0, sqrt(3.0));
  const vec2 hsz      = 0.5*sz;

  vec2 p1 = mod(p, sz)-hsz;
  vec2 p2 = mod(p - hsz, sz)-hsz;
  vec2 p3 = dot(p1, p1) < dot(p2, p2) ? p1 : p2;
  vec2 n = ((p3 - p + hsz)/sz);
  p = p3;

  n -= vec2(0.5);
  return round(n*2.0)*0.5;
}

float hexagon(vec2 p, float r) {
  const vec3 k = vec3(-0.866025404,0.5,0.577350269);
  p = abs(p);
  p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
  p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
  return length(p)*sign(p.y);
}

float shape(vec2 p) {
  return hexagon(p.yx, 0.4)-0.075;
}

float cellHeight(float h) {
  return 0.05*2.0*(-h);
}

vec3 cell(vec2 p, float h) {
  float hd = shape(p);

  const float he = 0.0075*2.0;
  float aa = he;
  float hh = -he*smoothstep(aa, -aa, hd);

  return vec3(hd, hh, cellHeight(h));
}

float height(vec2 p, float h) {
  return cell(p, h).y;
}

vec3 normal(vec2 p, float h) {
  vec2 e = vec2(4.0/RESOLUTION.y, 0);

  vec3 n;
  n.x = height(p + e.xy, h) - height(p - e.xy, h);
  n.y = height(p + e.yx, h) - height(p - e.yx, h);
  n.z = 2.0*e.x;

  return normalize(n);
}

vec3 planeColor(vec3 ro, vec3 rd, vec3 lp, vec3 pp, vec3 pnor, vec3 bcol, vec3 pcol) {
  vec3  ld = normalize(lp-pp);
  float dif  = pow(max(dot(ld, pnor), 0.0), 1.0);
  vec3 col = pcol;
  col = mix(bcol, col, dif);
  return col;
}

const mat2 rots[6] = mat2[](
    ROT(0.0*TAU/6.0)
  , ROT(1.0*TAU/6.0)
  , ROT(2.0*TAU/6.0)
  , ROT(3.0*TAU/6.0)
  , ROT(4.0*TAU/6.0)
  , ROT(5.0*TAU/6.0)
);

const vec2 off = vec2(1.0, 0.0);

const vec2 offs[6] = vec2[](
    off*rots[0]
  , off*rots[1]
  , off*rots[2]
  , off*rots[3]
  , off*rots[4]
  , off*rots[5]
  );

float cutSlice(vec2 p, vec2 off) {
  p.x = abs(p.x);
  off.x *= 0.5;

  vec2 nn = normalize(vec2(off));
  vec2 n  = vec2(nn.y, -nn.x);

  float d0 = length(p-off);
  float d1 = -(p.y-off.y);
  float d2 = dot(n, p);

  bool b = p.x > off.x && (dot(nn, p)-dot(nn, off)) < 0.0;

  return b ? d0 : max(d1, d2);
}

float hexSlice(vec2 p, int n) {
  n = 6-n;
  n = n%6;
  p *= rots[n];
  p = p.yx;
  const vec2 dim  = vec2((0.5)*2.0/sqrt(3.0), (0.5));
  return cutSlice(p, dim);
}

vec3 effect(vec2 p, vec2 q) {
  const float z = 0.327;
  float aa = 2.0/(z*RESOLUTION.y);

  p.yx = p;

  vec3 lp = vec3(3.0, 0.0, 1.0);

  p -= vec2(0.195, 0.);
  p /= z;

  float toff = 0.2*TIME;
  p.x += toff;
  lp.x += toff;

  vec2 hp  = p;
  vec2 hn  = hextile(hp);
  float hh = hash(hn);
  vec3 c   = cell(hp, hh);
  float cd = c.x;
  float ch = c.z;

  vec3 fpp = vec3(p, ch);
  vec3 bpp = vec3(p, 0.0);

  vec3 ro = vec3(0.0, 0.0, 1.0);
  vec3 rd = normalize(fpp-ro);

  vec3  bnor = vec3(0.0, 0.0, 1.0);
  vec3  bdif = lp-bpp;
  float bl2  = dot(bdif, bdif);

  vec3  fnor = normal(hp, hh);
  vec3  fld  = normalize(lp-fpp);

  float sf = 0.0;

  for (int i = 0; i < 6; ++i) {
    vec2  ioff= offs[i];
    vec2  ip  = p+ioff;
    vec2  ihn = hextile(ip);
    float ihh = hash(ihn);
    float ich = cellHeight(ihh);
    float iii = (ich-ch)/fld.z;
    vec3  ipp = vec3(hp, ch)+iii*fld;

    float hsd = hexSlice(ipp.xy, i);
    if (ich > ch) {
      sf += exp(-20.0*tanh_approx(1.0/(10.0*iii))*max(hsd+0., 0.0));
    }
  }

  const float sat = 0.23;
  vec3 bpcol = planeColor(ro, rd, lp, bpp, bnor, vec3(0.0), HSV2RGB(vec3(240.0/36.0, sat, 0.14)));
  vec3 fpcol = planeColor(ro, rd, lp, fpp, fnor, bpcol, HSV2RGB(vec3(240.0/36.0, sat, 0.19)));

  vec3 col = bpcol;
  col = mix(col, fpcol, smoothstep(aa, -aa, cd));
  col *= 1.0-tanh_approx(sf);

  float fo = exp(-0.025*max(bl2-0., 0.0));
  col *= fo;
  col = mix(bpcol, col, fo);


  return col;
}

void main() {
  vec2 q = gl_FragCoord.xy/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;

  vec3 col = effect(p, q);
  col = sRGB(col);

  gl_FragColor = vec4(col,1.0);
}
', 'data:image/jpeg;base64,', 'f6acdbc8-f257-4ad0-b343-39d5dbe9755a'::uuid, '2022-06-19');


INSERT INTO shader
(id, title, shader_code, preview_img, author_id, created_at)
VALUES('aac358de-aa4b-426d-86ed-c7d3f3cac1cf', 'Atom', 'mat2 r2d(float a) {
	float c = cos(a), s = sin(a);
    return mat2(
        c, s,
        -s, c
    );
}
void main() {
    float time = TIME;
    float rotTime = sin(time);

    vec3 color1 = vec3(0.8, 0.2, 0.);
    vec3 color2 = vec3(.0, 0.2, 0.8);

    vec2 uv = ( gl_FragCoord.xy -.5*RESOLUTION.xy )/RESOLUTION.y;

    vec3 destColor = vec3(2.0 * rotTime, .0, 0.5);
    float f = 10.15;
    const float maxIt = 18.0;
    vec3 shape = vec3(0.);
    for(float i = 0.0; i < maxIt; i++){
        float s = sin((time / 111.0) + i * cos(TIME*0.02+i)*0.05+0.05);
        float c = cos((time / 411.0) + i * (sin(time*0.02+i)*0.05+0.05));
        c += sin(TIME);
        f = (.005) / abs(length(uv / vec2(c, s)) - 0.4);
        f += exp(-400.*distance(uv, vec2(c,s)*0.5))*2.;
        // Mas Particulas
        f += exp(-200.*distance(uv, vec2(c,s)*-0.5))*2.;
        // Circulito
        f += (.008) / abs(length(uv/2. / vec2(c/4. + sin(time*4.), s/4.)));
        float idx = float(i)/ float(maxIt);
        idx = fract(idx*2.);
        vec3 colorX = mix(color1, color2,idx);
        shape += f * colorX;

        uv *= r2d(TIME*0.1 + cos(i*50.));
    }
    gl_FragColor = vec4(shape,1.0);
}', 'data:image/jpeg;base64,', 'f6acdbc8-f257-4ad0-b343-39d5dbe9755a'::uuid, '2022-06-19');


INSERT INTO shader
(id, title, shader_code, preview_img, author_id, created_at)
VALUES('ac113c0f-7c01-4c87-b317-18a1e706c0e4', 'Hologram', '#define PI 3.141592
#define TAU (PI*2.0)

float rand(vec2 n) {
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

float fbm(vec2 p) {
    float r = 0.0;
    float amp = 1.0;
    float freq = 1.0;
    for(int i = 0; i < 3; i++) {
        r += amp * noise(freq*p);
        amp *= 0.5;
        freq *= 1.0/0.5;
    }
    return r;
}

mat2 rot( float th ){ vec2 a = sin(vec2(1.5707963, 0) + th); return mat2(a, -a.y, a.x); }

float remap(float val, float im, float ix, float om, float ox)
{
    return clamp(om + (val - im) * (ox - om) / (ix - im), om, ox);
}

float cio(float t) {
	return t < 0.5
	? 0.5 * (1.0 - sqrt(1.0 - 4.0 * t * t))
	: 0.5 * (sqrt((3.0 - 2.0 * t) * (2.0 * t - 1.0)) + 1.0);
}

float animHeight(vec2 p)
{
    float s = 0., hs = 1.;
    float t = mod(TIME + 5.0, 7.);
    float tt = remap(t, 1., 2., 0., 1.);
    s = mix(0., .3, cio(tt));

    tt = remap(t, 3., 4., 0., 1.);
    p *= rot(cio(tt) * 3.);

    tt = remap(t, 4., 5., 0., 1.);
    s = mix(s, 1.0, cio(tt));

    tt = remap(t, 5., 6., 0., 1.);
    p *= rot(-cio(tt) * 3.);

    tt = remap(t, 6.5, 7., 0., 1.);
    p *= rot(cio(tt) * 1.);
    hs = mix(1., 0., cio(tt));

    float pls = (sin(t * TAU - PI*.5) * .5 + .5) * step(mod(t, 2.), 1.) * .2;
    return (fbm(p * s + t * .5) + pls) * hs;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return length(max(d,0.0));
}

vec2 rep( vec2 p, vec2 c)
{
    return mod(p,c)-0.5*c;
}

float map(vec3 p)
{
    float bd = length(p.xz) - 5.0;
    if (bd > 0.1) {
    	return bd;
    }
    vec2 id = floor(p.xz / 0.2);
    float height = animHeight(id * 0.2) * 0.5;
    p.xz = rep(p.xz, vec2(0.2));
    p.y -= height;
    float box = sdBox(p, vec3(0.03, height, 0.03));
    return max(box, bd) * .5;
}

vec2 trace(vec3 p, vec3 ray, float mx)
{
    float t = 0.0;
    vec3 pos;
    float dist;
    for (int i = 0; i < 128; i++) {
        pos = p + ray * t;
        dist = map(pos);
        if (dist < 0.002 || t > mx) {
        	break;
        }
        t += dist;
    }
    return vec2(t, dist);
}

vec3 getColor(vec3 p, vec3 ray)
{
    vec2 t = trace(p, ray, 100.0);
    vec3 pos = p + ray * t.x;
    if (t.x > 100.0) {
        return vec3(0.0);
    }
    return max(vec3(0.2, 0.5, 0.8) * 7.0 * pow(pos.y, 4.0) * smoothstep(0.0, -1.0, length(pos.xz) - 5.0), vec3(0.0));
}

mat3 camera(vec3 ro, vec3 ta, float cr )
{
	vec3 cw = normalize(ta - ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

float luminance(vec3 col)
{
    return dot(vec3(0.3, 0.6, 0.1), col);
}

vec3 acesFilm(const vec3 x) {
    const float a = 2.51;
    const float b = 0.03;
    const float c = 2.43;
    const float d = 0.59;
    const float e = 0.14;
    return clamp((x * (a * x + b)) / (x * (c * x + d ) + e), 0.0, 1.0);
}

void main()
{
    vec2 p = (gl_FragCoord.xy * 2.0 - RESOLUTION.xy) / min(RESOLUTION.x, RESOLUTION.y);

    float t = TIME * 0.1;
    vec3 ro = vec3(cos(t) * 10.0, 5.5, sin(t) * 10.0);
    vec3 ta = vec3(0.0, 1.0, 0.0);
    mat3 c = camera(ro, ta, 0.0);
    vec3 ray = c * normalize(vec3(p, 2.5));
    vec3 col = getColor(ro, ray);

    vec3 lp = vec3(0.0, 6.0, 0.0), rd = ray;

    float s = 7.5, vol = 0.0;
    for(int i = 0; i < 60; i++) {
        vec3 pos = ro + rd*s;

        vec3 v = -normalize(lp - pos);

    	float tt = -(lp.y-2.) / v.y;

        vec3 ppos = lp + v * tt;
	    vol += pow(animHeight(ppos.xz), 3.0) * 0.05 * smoothstep(0.0, 1.5, pos.y) * smoothstep(-1.0, -4.0, length(ppos.xz) - 5.0);

        s += 0.1;
    }

    col += 1.6*vec3(0.3*vol, 0.5*vol, vol);

    col = acesFilm(col * 0.5);
    col = pow(col, vec3(1.0/2.2));

    gl_FragColor = vec4(col,1.0);
}', 'data:image/jpeg;base64,', 'f6acdbc8-f257-4ad0-b343-39d5dbe9755a'::uuid, '2022-06-19');


INSERT INTO shader
(id, title, shader_code, preview_img, author_id, created_at)
VALUES('19acedbd-ec2b-423a-b82d-45bf92324a42', '2D Noise', '
vec2 hash( vec2 x )
{
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

vec3 noised( vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

#if 1
    // quintic interpolation
    vec2 u = f*f*f*(f*(f*6.0-15.0)+10.0);
    vec2 du = 30.0*f*f*(f*(f-2.0)+1.0);
#else
    // cubic interpolation
    vec2 u = f*f*(3.0-2.0*f);
    vec2 du = 6.0*f*(1.0-f);
#endif

    vec2 ga = hash( i + vec2(0.0,0.0) );
    vec2 gb = hash( i + vec2(1.0,0.0) );
    vec2 gc = hash( i + vec2(0.0,1.0) );
    vec2 gd = hash( i + vec2(1.0,1.0) );

    float va = dot( ga, f - vec2(0.0,0.0) );
    float vb = dot( gb, f - vec2(1.0,0.0) );
    float vc = dot( gc, f - vec2(0.0,1.0) );
    float vd = dot( gd, f - vec2(1.0,1.0) );

    return vec3( va + u.x*(vb-va) + u.y*(vc-va) + u.x*u.y*(va-vb-vc+vd),   // value
                 ga + u.x*(gb-ga) + u.y*(gc-ga) + u.x*u.y*(ga-gb-gc+gd) +  // derivatives
                 du * (u.yx*(va-vb-vc+vd) + vec2(vb,vc) - va));
}

void main()
{
    vec2 p = (2.0*gl_FragCoord.xy-RESOLUTION.xy)/RESOLUTION.y;
    vec3 n = noised( 8.0*p + TIME*4.0 );
    vec3 col = 0.5 + 0.5*((p.x>0.0) ? n.yzx : n.xxx);

	gl_FragColor = vec4( col, 1.0 );
}', 'data:image/jpeg;base64,', 'f6acdbc8-f257-4ad0-b343-39d5dbe9755a'::uuid, '2022-06-19');


INSERT INTO shader
(id, title, shader_code, preview_img, author_id, created_at)
VALUES('666b96af-33ac-4407-a9e8-5907052cab24', 'Colored Parcticles', '#define RADIUS 0.2
#define SMOOTH_BOORDER 0.01
#define PI   3.1415926
#define PId2 1.5707963
#define PI2  6.2831853
#define NUMCIRCLES 86
#define TIME_SCALE 2.0


vec2 random( float x, float y )
{
    vec2 K1 = vec2(
        23.14069263277926,
         2.665144142690225
    );
    return abs(fract( vec2(cos( x * K1.x), sin( y * K1.y + 166.6))));
}

vec2 generateRandomShift(float xSeed, float ySeed, float time, vec2 ratio)
{
    return (2.0 *random(ceil(time/PI2) + xSeed, ceil(time/PI2) + ySeed) - 1.0) * ratio;
}

float sinWave01(float time)
{
    return 0.5 + 0.5 * sin(time - PId2);
}

float calculateCircle(vec2 uv, float r, float blur)
{
    float dist = length(uv);
    return smoothstep(r + blur, r, dist);
}

void main()
{
    vec2 uv = (2.0 * gl_FragCoord.xy - RESOLUTION.xy)/ RESOLUTION.y;

    vec2 ratio = RESOLUTION.xy/ RESOLUTION.y;

    float col;
    for(int i = 0; i < NUMCIRCLES; ++i)
    {
        float timeShift = 2.0*float(i);
        float time = TIME_SCALE*(TIME + timeShift);
        vec2 shift =  generateRandomShift(2.0*float(i), float(i + 56), time, ratio);
        col += calculateCircle(uv - shift, RADIUS * sinWave01(time), SMOOTH_BOORDER) *
                              (1.0 - sinWave01(time));
    }

    vec3 sincol = 0.5 + 0.5*cos(TIME+uv.xyx+vec3(0,2,4));

    gl_FragColor = vec4(sincol * (clamp(col, 0.0, 1.0)), 1.0);
}', 'data:image/jpeg;base64,', 'f6acdbc8-f257-4ad0-b343-39d5dbe9755a'::uuid, '2022-06-19');


